// 


import React, { useState, useEffect } from 'react';
import UploadNovelForm from './UploadNovelForm';

function App() {
  // Initialize with some default novels
  const [novels, setNovels] = useState([
    {
      title: 'The Great Gatsby',
      author: 'F. Scott Fitzgerald',
      genre: 'Classic',
      summary: 'A story about the mysterious millionaire Jay Gatsby and his obsession with Daisy Buchanan.',
      imageUrl: 'https://example.com/gatsby.jpg'
    },
    {
      title: '1984',
      author: 'George Orwell',
      genre: 'Dystopian',
      summary: 'A depiction of a totalitarian regime in a future society governed by Big Brother.',
      imageUrl: 'https://example.com/1984.jpg'
    },
    {
      title: 'To Kill a Mockingbird',
      author: 'Harper Lee',
      genre: 'Fiction',
      summary: 'A novel set in the American South during the 1930s, focusing on racial injustice.',
      imageUrl: 'https://example.com/mockingbird.jpg'
    }
  ]);

  const [selectedNovel, setSelectedNovel] = useState(null); // Selected novel for details view
  const [view, setView] = useState('home'); // Tracks current view (home, upload, details)
  const [searchQuery, setSearchQuery] = useState(''); // Search query state

  // Load novels from localStorage when the component first mounts
  useEffect(() => {
    const savedNovels = JSON.parse(localStorage.getItem('novels')) || [];
    setNovels(savedNovels.length > 0 ? savedNovels : novels); // Load saved novels or default ones
  }, []);

  // Save novels to localStorage whenever the novels state changes
  useEffect(() => {
    localStorage.setItem('novels', JSON.stringify(novels));
  }, [novels]);

  // Function to add a new novel and switch back to home view
  const addNovel = (novel) => {
    setNovels([...novels, novel]); // Add the new novel to the list
    setView('home'); // Switch back to the home view immediately after uploading
  };

  const selectNovel = (novel) => {
    setSelectedNovel(novel);
    setView('details'); // Switch to the details view
  };

  // Filter novels based on the search query
  const filteredNovels = novels.filter((novel) =>
    novel.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div>
      {view === 'home' && (
        <div>
          <h1>Novel List</h1>

          {/* Search Bar */}
          <input
            type="text"
            placeholder="Search for a novel"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />

          <button onClick={() => setView('upload')}>Upload a Novel</button>

          <ul>
            {filteredNovels.length > 0 ? (
              filteredNovels.map((novel, index) => (
                <li key={index} onClick={() => selectNovel(novel)}>
                  <h2>{novel.title}</h2>
                  <p><strong>Author:</strong> {novel.author}</p>
                  <p><strong>Genre:</strong> {novel.genre}</p>
                  <p><strong>Summary:</strong> {novel.summary}</p>
                  {novel.imageUrl && <img src={novel.imageUrl} alt={novel.title} style={{ width: '100px', height: '150px' }} />}
                </li>
              ))
            ) : (
              <p>No novels found</p>
            )}
          </ul>
        </div>
      )}

      {view === 'upload' && <UploadNovelForm addNovel={addNovel} />} {/* Pass addNovel as prop */}

      {view === 'details' && selectedNovel && (
        <div>
          <h1>{selectedNovel.title}</h1>
          <p><strong>Author:</strong> {selectedNovel.author}</p>
          <p><strong>Genre:</strong> {selectedNovel.genre}</p>
          <p><strong>Summary:</strong> {selectedNovel.summary}</p>
          {selectedNovel.imageUrl && <img src={selectedNovel.imageUrl} alt={selectedNovel.title} style={{ width: '200px', height: '300px' }} />}
          <button onClick={() => setView('home')}>Back to Home</button>
        </div>
      )}
    </div>
  );
}

export default App;
