import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate, useParams } from 'react-router-dom';
import UploadNovelForm from './UploadNovelForm';
import './App.css';

function App() {
  const [novels, setNovels] = useState([]);
  const [darkMode, setDarkMode] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    const savedNovels = JSON.parse(localStorage.getItem('novels')) || [];
    setNovels(savedNovels);
  }, []);

  const addNovel = (novel) => {
    const updatedNovels = [...novels, novel];
    setNovels(updatedNovels);
    localStorage.setItem('novels', JSON.stringify(updatedNovels));
    navigate('/');
  };

  const removeNovel = (indexToRemove) => {
    const updatedNovels = novels.filter((novel, index) => index !== indexToRemove);
    setNovels(updatedNovels);
    localStorage.setItem('novels', JSON.stringify(updatedNovels));
  };

  const filteredNovels = novels.filter((novel) =>
    novel.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const toggleDarkMode = () => setDarkMode(!darkMode);

  return (
    <div className={darkMode ? 'dark-mode' : ''}>
      <Router>
        <nav className="navbar">
          <Link to="/" className="nav-title">
            <h1>Novel Blog</h1>
          </Link>
          <input
            type="text"
            className="search-bar"
            placeholder="Search for a novel..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button onClick={toggleDarkMode} className="mode-toggle">
            {darkMode ? 'Light Mode' : 'Dark Mode'}
          </button>
        </nav>

        <Routes>
          <Route
            path="/"
            element={
              <div className="container">
                <div className="novel-grid">
                  {filteredNovels.length > 0 ? (
                    filteredNovels.map((novel, index) => (
                      <div key={index} className="novel-card">
                        <Link to={`/novel/${index}`} className="novel-card-link">
                          <img src={novel.imageUrl} alt={novel.title} className="novel-img" />
                          <div className="novel-info">
                            <h2>{novel.title}</h2>
                            <p><strong>Author:</strong> {novel.author}</p>
                            <p><strong>Genre:</strong> {novel.genre}</p>
                          </div>
                        </Link>
                        {/* Delete Button */}
                        <button onClick={() => removeNovel(index)} className="delete-button">
                          Delete
                        </button>
                      </div>
                    ))
                  ) : (
                    <p>No novels found</p>
                  )}
                </div>
                <button className="upload-button" onClick={() => navigate('/upload')}>
                  Upload a Novel
                </button>
              </div>
            }
          />
          <Route path="/upload" element={<UploadNovelForm addNovel={addNovel} />} />
          <Route path="/novel/:id" element={<NovelDetails novels={novels} />} />
        </Routes>
      </Router>
    </div>
  );
}

function NovelDetails({ novels }) {
  const { id } = useParams();
  const novel = novels[id];

  if (!novel) {
    return <div className="container"><p>Novel not found.</p></div>;
  }

  return (
    <div className="container novel-details">
      <h2>{novel.title}</h2>
      <img src={novel.imageUrl} alt={novel.title} className="novel-details-img" />
      <p><strong>Author:</strong> {novel.author}</p>
      <p><strong>Genre:</strong> {novel.genre}</p>
      <p><strong>Summary:</strong> {novel.summary}</p>
    </div>
  );
}

export default App;
