import React, { useState } from 'react';
import UploadNovelForm from './UploadNovelForm';
import app from "./App.css";

function App() {
  const [novels, setNovels] = useState([]);
  const [selectedNovel, setSelectedNovel] = useState(null);
  const [view, setView] = useState('home'); // home, upload, details

  useEffect(() => {
    const savedNovels = JSON.parse(localStorage.getItem('novels')) || [];
    setNovels(savedNovels);
  }, []);

  useEffect(() => {
    localStorage.setItem('novels', JSON.stringify(novels));
  }, [novels]);




  const addNovel = (novel) => {
    setNovels([...novels, novel]);
    setView('home');
  };

  const selectNovel = (novel) => {
    setSelectedNovel(novel);
    setView('details');
  };

  return (
    <div>
      {view === 'home' && <button onClick={() => setView('upload')}>Upload a Novel</button>}
      {view === 'upload' && <UploadNovelForm addNovel={addNovel} />}
      {view === 'details' && selectedNovel && (
        <div>
          <h1>{selectedNovel.title}</h1>
          <p>{selectedNovel.author}</p>
          <p>{selectedNovel.genre}</p>
          <p>{selectedNovel.summary}</p>
          <img src={selectedNovel.imageUrl} alt={selectedNovel.title} />
        </div>
      )}
    </div>
  );
}

export default App;
